INSERT INTO movies(title,year,description,poster_url, screenshot_url) VALUES ('Interview with the Vampire: The Vampire Chronicles', 1994,'It hasn/t even been a year since a plantation owner named Louis lost his wife in childbirth. Both his wife and the infant died, and now he has lost his will to live. A vampire named Lestat takes a liking to Louis and offers him the chance to become a creature of the night: a vampire. Louis accepts, and Lestat drains Louis mortal blood and then replaces it with his own, turning Louis into a vampire. Louis must learn from Lestat the ways of the vampire.',  'https://ia.media-imdb.com/images/M/MV5BYThmYjJhMGItNjlmOC00ZDRiLWEzNjUtZjU4MjA3MzY0MzFmXkEyXkFqcGdeQXVyNTI4MjkwNjA@._V1_SY1000_CR0,0,667,1000_AL_.jpg', 'https://movieplayer.net-cdn.it/images/2009/08/23/claudia-kirsten-dunst-louis-brad-pitt-e-lestat-tom-cruise-in-una-scena-del-film-interview-with-the-vampire-127952.jpg');
INSERT INTO movies(title,year,description,poster_url, screenshot_url) VALUES ('V for Vendetta',2005,  'In a future British tyranny, a shadowy freedom fighter, known only by the alias of "V", plots to overthrow it with the help of a young woman.', 'https://ia.media-imdb.com/images/M/MV5BYzllMjJkODAtYjMwMi00YmNhLWFhYzAtZjZjODg5YzEwOGUwXkEyXkFqcGdeQXVyNjU0OTQ0OTY@._V1_SY999_CR0,0,679,999_AL_.jpg','https://nerdist.com/wp-content/uploads/2016/05/v-for-vendetta-FEAT.jpg');
